(66498, -1, 'thread_prune_move_originaldaysolder_title', 'cphelptext', 'Original post date is at least X days ago', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66499, -1, 'thread_prune_move_originaldaysnewer_text', 'cphelptext', 'The thread must not have been created before this date in order to be pruned / moved.  You can use this setting in conjunction with the previous setting to create a window of time that you want to be pruned / moved.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
