(64555, -1, 'email_start_from_text', 'cphelptext', 'This is the address that the email is to be from. The email address set as the webmaster''s address is pre-filled in, but you can enter any address you wish here.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64556, -1, 'email_start_from_title', 'cphelptext', 'From', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
