(64565, -1, 'faq_add_product_text', 'cphelptext', 'This controls what product this FAQ is associated with. You do not need to worry about this option unless you are planning on making an addon for vBulletin to be released.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64566, -1, 'faq_add_product_title', 'cphelptext', 'Product', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64567, -1, 'faq_add_faq_text', 'cphelptext', 'This is a short name for the FAQ and is only used internally. It may only contain letters, numerals and the underscore character and must start with a letter.', 'vbulletin', 'vBulletin Solutions', 1205940980, '3.7.0 Release Candidate 1', 0),
