(66965, -1, 'usergroup_add_edit_canuseavatar_text', 'cphelptext', 'This permission will allow members of this group to upload a custom avatar on the <a href="../profile.php?do=editavatar" target="_blank">Edit Avatar</a> page in their User CP.  Custom avatars are uploaded from the user''s computer or from a URL and are stored on your web server.<br /><br />\r\n\r\nAvatars must be enabled in your <a href="options.php?dogroup=userpix&amp;do=options" target="_blank">vBulletin options</a> for this setting to have any effect.  There are also some avatar settings on the same page in your vBulletin options.', 'vbulletin', 'vBulletin Solutions', 1205430879, '3.7.0 Release Candidate 1', 0),
(66645, -1, 'user_add_edit_autosubscribe_text', 'cphelptext', 'This option determines the default level of subscription when this user makes a post.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
