(66957, -1, 'usergroup_add_edit_canpostattachment_text', 'cphelptext', 'vBulletin allows users to upload files to their posts.  This permission will enable that functionality for members of this group.  Users can upload files by clicking the "Manage Attachments" button on the posting form.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66158, -1, 'ranks_add_edit_rankhtml_title', 'cphelptext', 'Rank Text', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
