(65687, -1, 'options_options_memberlistfields_text', 'cphelptext', 'Check what profile fields you want to appear on the <a href="../memberlist.php" target="_blank">member list page</a>.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(65688, -1, 'options_options_memberlistfields_title', 'cphelptext', 'Member List Field Options', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(65689, -1, 'options_options_memberlistperpage_text', 'cphelptext', 'This setting determines the number of members displayed per page on the members list.<br />\r\n<br />\r\n<b>Note: </b>This number must be between 1 and 100.', 'vbulletin', 'vBulletin Solutions', 1309989121, '4.1.5 Beta 1', 0),
