INSERT INTO `phrase` (`phraseid`, `languageid`, `varname`, `fieldname`, `text`, `product`, `username`, `dateline`, `version`, `importphraseid`) VALUES
(64017, -1, 'admininfraction_editgroup_usergroupid_text', 'cphelptext', 'This is the usergroup for which this infraction group applies to.  Setting this to <em>-- All Usergroups--</em> will apply this infraction group to all users. Otherwise this infraction group will be added to a user''s infraction groups when their Primary Usergroup matches this one and the user accumulates infraction points greater than or equal to the points given for this group.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64018, -1, 'admininfraction_editgroup_usergroupid_title', 'cphelptext', 'Primary Usergroup', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64019, -1, 'admininfraction_editgroup_orusergroupid_text', 'cphelptext', 'When this infraction group is given to an user, this user group will be used to apply permissions from.\r\n\r\nInfraction groups work in the opposite manner of normal usergroups.  When an infraction usergroup is given to a user, the user''s normal permissions will be determined and then the users combined infraction groups permissions will be determined.\r\n\r\nIf any permission in an infraction usergroup is set to <strong>No</strong> then the user will lose that permission.\r\n\r\n<strong>No</strong> permissions override <strong>Yes</strong> permissions.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64020, -1, 'admininfraction_editgroup_orusergroupid_title', 'cphelptext', 'Override with Permissions from Usergroup', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64021, -1, 'admininfraction_editlevel_title_text', 'cphelptext', 'This is the title of the infraction level.  It is displayed on the screen from which infractions are given and it is also send to user''s as the title of their notification pm/email.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64022, -1, 'admininfraction_editlevel_title_title', 'cphelptext', 'Title', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64023, -1, 'admininfraction_editlevel_expires_text', 'cphelptext', 'Infractions can either be set to expire within a defined timeframe or they can be set to never expire.  When an infraction expires, any associated points that were given with the infraction will be removed from the user''s points total and the user''s infraction groups will be recalculated.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64024, -1, 'admininfraction_editlevel_expires_title', 'cphelptext', 'Expires', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64025, -1, 'admininfraction_editlevel_points_text', 'cphelptext', 'This is the number of points that are awarded for an infraction.  Every infraction level must award at least 1 point.  A users combined point score is used to determine which infraction groups to place him or her into.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64026, -1, 'admininfraction_editlevel_points_title', 'cphelptext', 'Points', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64027, -1, 'admininfraction_editlevel_warning_text', 'cphelptext', 'Infraction Levels can have the option of allowing a warning to be given.  If an infraction allows warnings, the user giving the infraction will have the choice of giving a warning or giving the infraction''s allotted points.\r\n\r\nWarnings do not add any points to a user and have no effect on the user''s infraction groups.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64028, -1, 'admininfraction_editlevel_warning_title', 'cphelptext', 'Warning', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64029, -1, 'admininfraction_editlevel_extend_text', 'cphelptext', 'When this option is enabled a user will receive extended expiration times if given a second infraction that matches an existing active infraction.\r\n\r\nFor example, a user is given an infraction for spamming posts that expires in 1 day.  An hour later this user is given another spamming posts infraction.  Since the user already had an active spamming posts infraction, this new infraction''s expiration time is added to the end time of the previous infraction.  Effectively, this new infraction  will now end 1 day and 23 hours later instead of the default of 1 day. If the user was given a third infraction, it would be added to the end of the second infraction as well.', 'vbulletin', 'vBulletin Solutions', 1155944185, '3.6.1', 0),
(64030, -1, 'admininfraction_editlevel_extend_title', 'cphelptext', 'Extend', 'vbulletin', 'vBulletin Solutions', 1155944185, '3.6.1', 0),
(64031, -1, 'admininfraction_modify_text', 'cphelptext', 'From this screen you can view and modify your current infraction levels and infraction groups.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64032, -1, 'admininfraction_modify_title', 'cphelptext', 'User Infractions', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(64033, -1, 'adminlog_text', 'cphelptext', '<b>Related General Help Topics:</b><br />\r\n<a href="help.php?s=&amp;do=answer&amp;page=stats&amp;pageaction=&amp;option=">Statistics</a><br />\r\n<a href="help.php?s=&amp;do=answer&amp;page=adminlog&amp;pageaction=&amp;option=">Control Panel Log</a><br />\r\n<a href="help.php?s=&amp;do=answer&amp;page=modlog&amp;pageaction=&amp;option=">Moderator Log</a><br />\r\n<a href="help.php?s=&amp;do=answer&amp;page=cronlog&amp;pageaction=&amp;option=">Scheduled Task Log</a><br /><br />\r\n\r\n<b>Control Panel Log:</b><br />\r\nThe Administrator''s Log allows you to keep tabs on your co-admins, or have a record of IP address(es) and activity in case of a break-in. Each time an action is performed in the Administrator''s Control Panel, it is logged. The information stored includes the username, their IP address, the script involved, the script section in use, and additional information which varies depending on the action being performed.<br /><br />\r\n\r\nYou can restrict other administrators from viewing and/or pruning the Administrator''s Log by modifying your includes/config.php file. (see <a href="help.php?do=answer&amp;page=adminlog&amp;pageaction=restrict">Restricting Access to the Admin Log</a>)', 'vbulletin', 'vBulletin Solutions', 1205431035, '3.7.0 Release Candidate 1', 0),
(64034, -1, 'adminlog_title', 'cphelptext', 'About Control Panel Log', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64035, -1, 'adminlog_choose_text', 'cphelptext', 'From this screen you can view, and if you have permission, prune, your admin log.<br /><br />\r\n\r\nUnder Viewing, you have four options to limit your log search. These are explained below.<br /><br />\r\n\r\nOnce you have made your selection, press View Logs.<br /><br />\r\n\r\nIf you wish to prune your log, the pruning selector is very similar. The options are also described below.<br /><br />\r\n\r\nKeep in mind that these options are cumulative. If you select JoeBlow for option #2 and enter 40 for option #3, you will remove all entries by JoeBlow that are more than 40 days old.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64036, -1, 'adminlog_choose_title', 'cphelptext', 'Selecting Admin Log Viewing Options', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64037, -1, 'adminlog_choose_enddate_text', 'cphelptext', 'You can filter log entries that were created within a time period.  Here you can select the end of that time period.', 'vbulletin', 'vBulletin Solutions', 1220959288, '3.8.0 Alpha 1', 0),
