(64178, -1, 'attachment_edit_upload_title', 'cphelptext', 'Upload', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64179, -1, 'attachment_edit_url_text', 'cphelptext', 'Specify a complete URL to a file to have vBulletin retrieve the file and attach it to the post.  Example:<br /><br />\r\n\r\nhttp://images.vbulletin.com/images_vb3/misc/vbulletin3_logo_white.gif', 'vbulletin', 'vBulletin Solutions', 1205943297, '3.7.0 Release Candidate 1', 0),
