(64182, -1, 'attachment_edit_a_filename_title', 'cphelptext', 'Filename', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64183, -1, 'attachment_edit_a_counter_text', 'cphelptext', 'This is the number of times that the attachment has been viewed (or downloaded).', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64184, -1, 'attachment_edit_a_counter_title', 'cphelptext', 'Views', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64185, -1, 'attachment_edit_a_visible_text', 'cphelptext', 'If you select No to this option, it will not be visible on showthread.php with the post. Instead it will act as if it is waiting for moderation; you can find it under Attachments > Moderate.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
