INSERT INTO `phrase` (`phraseid`, `languageid`, `varname`, `fieldname`, `text`, `product`, `username`, `dateline`, `version`, `importphraseid`) VALUES
(64187, -1, 'attachment_intro_text', 'cphelptext', 'On this screen you have several different options for searching attachments. (see <a href="help.php?do=answer&amp;page=attachment&amp;pageaction=search">Results of Searching Attachments</a>)<br /><br />\r\n\r\nAt the top are several links to common searches. These are:<br />\r\n<b>Largest Attachments</b>: This searches attachments, ordering them by size, largest first.<br /><br />\r\n\r\n<b>Most Popular Attachments</b>: This sorts a search of attachments by number of downloads, most downloads first.<br /><br />\r\n\r\n<b>Newest Attachments</b>: This searches attachments and sorts them by date, newest first.<br /><br />\r\n\r\n<b>Oldest Attachments</b>: Selecting this shows all attachments, sorted by date with oldest first.<br /><br /><br />\r\n\r\nAttachment Pruning finds attachments older than a certain date, with 30 days being the default. Enter the desired age and press View Results.<br /><br />\r\n\r\nUnder Advanced Attachment Search, you can use specific criteria to do a custom search. The criteria entry boxes are described below.', 'vbulletin', 'vBulletin Solutions', 1205430963, '3.7.0 Release Candidate 1', 0),
(64188, -1, 'attachment_intro_title', 'cphelptext', 'Searching for Specific Attachments', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64189, -1, 'attachment_intro_prunedate_text', 'cphelptext', 'You can search for attachments older than X days and on the following screen quickly prune them.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64190, -1, 'attachment_intro_prunedate_title', 'cphelptext', 'Pruning: Find All Attachments Older Than X Days', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64191, -1, 'attachment_intro_filename_text', 'cphelptext', 'This is a case-insensitive search of the filenames to see if the string you enter is contained in it. This includes extensions (e.g. .jpg or .txt).', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64192, -1, 'attachment_intro_filename_title', 'cphelptext', 'Filename', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64193, -1, 'attachment_intro_attachedby_text', 'cphelptext', 'If you wish to limit the search to attachments posted by one user, enter their username here.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64194, -1, 'attachment_intro_attachedby_title', 'cphelptext', 'Attached By', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64195, -1, 'attachment_intro_datelinebefore_text', 'cphelptext', 'Enter a date that attachments must have been attached before. The format is YYYY-MM-DD HH:MM:SS. For example, 2002-04-30 12:02:01. Use a 24 hour clock; that is, convert 01:02:02 PM into 13:02:02.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64196, -1, 'attachment_intro_datelinebefore_title', 'cphelptext', 'Attached Before', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64197, -1, 'attachment_intro_datelineafter_text', 'cphelptext', 'You can also set a starting date, where the attachment must have been attached after this date. The format is YYYY-MM-DD HH:MM:SS. For example, 2002-04-30 12:02:01. Use a 24 hour clock; that is, convert 01:02:02 PM into 13:02:02. Note if you use both Attached Before and Attached After, Attached After must be a date before Attached Before or there will be no search period!', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64198, -1, 'attachment_intro_datelineafter_title', 'cphelptext', 'Attached After', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64199, -1, 'attachment_intro_downloadsmore_text', 'cphelptext', 'Finds attachments that have had more than X downloads, X being the number you specify.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64200, -1, 'attachment_intro_downloadsmore_title', 'cphelptext', 'More Than X Downloads', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64201, -1, 'attachment_intro_downloadsless_text', 'cphelptext', 'Finds attachments that have had less than X downloads, X being the number you specify. Note if you use both More Than and Less Than X Downloads, the More Than number must be less than the Less Than number or there will be no search range!', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64202, -1, 'attachment_intro_downloadsless_title', 'cphelptext', 'Less Than X Downloads', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64203, -1, 'attachment_intro_sizemore_text', 'cphelptext', 'Enter the size, in bytes, that the attachment must be larger than. Remember this is in bytes, <i>not</i> KiloBytes.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64204, -1, 'attachment_intro_sizemore_title', 'cphelptext', 'File-size Greater Than', 'vbulletin', 'vBulletin Solutions', 1311525963, '4.1.7 Beta 1', 0),
(64205, -1, 'attachment_intro_sizeless_text', 'cphelptext', 'Enter the size, in bytes, that the attachment must be smaller than. Remember this is in bytes, <i>not</i> KiloBytes! Note that if you are using both File-size Greater Than and File-size Less Than, File-size Greater Than must be less than File-size Less Than or there will be no range to search!', 'vbulletin', 'vBulletin Solutions', 1311525963, '4.1.7 Beta 1', 0),
(64206, -1, 'attachment_intro_sizeless_title', 'cphelptext', 'File-size Less Than', 'vbulletin', 'vBulletin Solutions', 1311525963, '4.1.7 Beta 1', 0),
(64207, -1, 'attachment_intro_visible_text', 'cphelptext', 'If you have attachment moderation and have attachments which have not been moderated yet, or have manually edited an attachment and turned its visibility to Off, setting this option to <b>No</b> will find only those attachments.', 'vbulletin', 'vBulletin Solutions', 1204936607, '3.7.0 Release Candidate 1', 0),
(64208, -1, 'attachment_intro_visible_title', 'cphelptext', 'Attachment Is Visible', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64209, -1, 'attachment_intro_orderby_text', 'cphelptext', 'You can order your search results by several options, either ascending or descending. They are Attached By, Downloads, Filename, File-size, Time Attached, Visible. This will not affect your search, only how the results are displayed.', 'vbulletin', 'vBulletin Solutions', 1311525963, '4.1.7 Beta 1', 0),
(64210, -1, 'attachment_intro_orderby_title', 'cphelptext', 'Order By', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64211, -1, 'attachment_intro_results_text', 'cphelptext', 'Determines how many results are shown on each page after you click Find Attachments. It is recommended you do not set this too high for performance reasons.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64212, -1, 'attachment_intro_results_title', 'cphelptext', 'Attachments to Show Per Page', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64213, -1, 'attachment_massdelete_text', 'cphelptext', 'This is a confirmation that you want to delete the attachments you selected on the previous page.  Click the "Yes" button to perform the deletion.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64214, -1, 'attachment_massdelete_title', 'cphelptext', 'Delete Attachments', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64215, -1, 'attachment_removetype_text', 'cphelptext', 'This is a confirmation that you want to delete this attachment type.  Please confirm the correct attachment type below and click the "Yes" button to continue.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64216, -1, 'attachment_removetype_title', 'cphelptext', 'Delete Attachment Type', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64217, -1, 'attachment_search_text', 'cphelptext', 'On this screen you are given a lot of information about each attachment that was found as a result of your search. (see <a href="help.php?do=answer&amp;page=attachment.php&amp;pageaction=intro">Searching for Specific Attachments</a>)<br /><br />\r\n\r\nFrom this screen, you are shown the following:<br />\r\n<b>Filename</b>: This is the filename of the attachment. Clicking on the filename will show you the attachment itself if it is an image, or prompt you to download the file if it is another type of attachment.<br /><br />\r\n\r\n<b>User Name</b>: This is the user that posted the attachment. Clicking their name will take you to edit their profile.<br /><br />\r\n\r\n<b>Date</b>: This is the date the attachment was posted. Next to the date is a link to view the post that the attachment belongs to.<br /><br />\r\n\r\n<b>Size</b>: This is the size, in KB (KiloBytes), of the attachment.<br /><br />\r\n\r\n<b>Downloads</b>: This is how many times the attachment has been downloaded.<br /><br />\r\n\r\nAt the extreme left is a column of checkboxes. You can check them individually or click the box in the header row to select them all. Clicking "Delete Checked Attachments" will remove the selected attachments.<br /><br />\r\n\r\nAt the extreme right is a link to delete an attachment.', 'vbulletin', 'vBulletin Solutions', 1205430963, '3.7.0 Release Candidate 1', 0),
(64218, -1, 'attachment_search_title', 'cphelptext', 'Results of Searching Attachments', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64219, -1, 'attachment_stats_text', 'cphelptext', 'On this screen you are given several "top 5s" about the attachments on your forum as well as some general statistics.<br /><br />\r\n\r\nFrom top to bottom:\r\n<b>Statistics:</b><br />\r\n<b>Total Attachments</b>: This is the total number of attachments on your forum.<br /><br />\r\n\r\n<b>Disk Space Used</b>: This is how much disk space is consumed by all attachments combined.<br /><br />\r\n\r\n<b>Storage Type</b>: Where the attachments are being stored, either in the database or the file system, and if they are in the file system, the actual path to them.<br /><br />\r\n\r\n<b>Average Attachment File-size</b>: This is the average size of an attachment, determined by dividing the number of attachments into the total disk space used.<br /><br />\r\n\r\nUnder <b>Five Most Popular Attachments</b>, the columns are:<br />\r\n<b>Rank</b>: The rank of the attachment.<br /><br />\r\n\r\n<b>Filename</b>: The filename of the attachment. Clicking on it will show you the attachment, if it is an image, or prompt you to download it if it is another file type.<br /><br />\r\n\r\n<b>User Name</b>: This is who posted the attachment. Clicking on the username will bring up the edit user page for that individual.<br /><br />\r\n\r\n<b>Downloads</b>: This is how many times the attachment has been downloaded by users.<br /><br />\r\n\r\nTo the left are several other options.<br />\r\n<b>View Post</b> shows you the post that the attachment was attached to.<br /><br />\r\n\r\n<b>Delete</b>: Choose this to remove the attachment.<br /><br />\r\n\r\nThe <b>Five Largest Attachments</b> is nearly identical, save for having a Size column instead of a Downloads column. The Size column indicates the actual byte size of the attachment, in KB (KiloBytes).<br /><br />\r\n\r\n<b>Five Users Whose Attachments Take Up The Most Space</b> shows you at a glance who is attachment-happy. For each user in the top five (if there are not five users who have made attachments, only those who have will be shown) their name, number of attachments, and the total size of those attachments is displayed. Clicking View Attachments at the end of each row will find all of the attachments that the user has posted. (see <a href="help.php?do=answer&amp;page=attachment&amp;pageaction=search">Searching Attachments</a>)', 'vbulletin', 'vBulletin Solutions', 1311525963, '4.1.7 Beta 1', 0),
(64220, -1, 'attachment_stats_title', 'cphelptext', 'Viewing Attachment Statistics', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64221, -1, 'attachment_storage_switchtype_text', 'cphelptext', 'This process will allow you to move your attachments from the database into the file system, or, if they are in the file system, to a different directory in the file system or back into the database. Please note that if you do not have enough disk space in the intended destination, the process will fail, but your attachments will be safe as tests are done first to ensure there is enough space.<br />\r\nAttachments will not be removed from the database or filesystem and your storage type setting will not be changed until the end of the process.<br />\r\nIf the process aborts before the end, you will need to free up space or prune attachments before trying again.<br /><br />\r\n\r\nIf you are moving attachments into the file system, it is recommended that you do *not* place the folder under your web root (usually named public_html or www or www_root) because people will be able to bypass vBulletin''s permission system and download them. Also, you will need to make the folder writeable by PHP. The easiest way to do this is CHMOD it 777 (consult your FTP, Telnet, or SSH documentation for more info on how to do this). We do not recommended that you put a folder that is CHMODed 777 under your web root - another good reason to have it located above the document root.<br /><br />\r\n\r\nIf you are moving attachments from the file system into the database, you must have enough storage space on the drive and/or partition where your database is located.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64222, -1, 'attachment_storage_switchtype_title', 'cphelptext', 'Switching Attachments from the Database to the File System and vice-versa', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64223, -1, 'attachment_switchtype_attachpath_text', 'cphelptext', 'This is the file path to store attachments in.\r\n\r\nThis file path should be readable AND writeable by your web server (usually CHMOD 777). It should be above your web documents root or users will be able to bypass vBulletin''s permissions system and download attachments anyway.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
