INSERT INTO `phrase` (`phraseid`, `languageid`, `varname`, `fieldname`, `text`, `product`, `username`, `dateline`, `version`, `importphraseid`) VALUES
(66559, -1, 'user_add_edit_adminavatar_text', 'cphelptext', 'This option allows the user to keep their custom avatar in the event that they lose permission to upload custom avatars.\r\n\r\nIf the user is moved to an usergroup that doesn''t allow custom avatars; the user''s avatar will not be visible in posts, their profile, and the member''s list unless this option is enabled.\r\n\r\nThis option does not allow the user to change their avatar. Usergroup permission is required to accomplish that task.\r\n\r\nIf the admin changes or gives an a user a custom avatar when the user doesn''t have usergroup permission to have one, this option will be automatically enabled.', 'vbulletin', 'vBulletin Solutions', 1144687826, '3.6.0 Beta 1', 0),
(65370, -1, 'options_options_vbforum_url_title', 'cphelptext', 'Forum Component URL', 'vbulletin', 'vBulletin Solutions', 1291743931, '4.1.1 Alpha 1', 0),
(65371, -1, 'options_options_vm_allowed_bbcode_text', 'cphelptext', 'Use this option to specify which (if any) BB code tags your visitors may use when composing visitor messages.<br />\r\n<br />\r\nBy default, Basic (B, I, U), Color and Link (URL, EMAIL, THREAD, POST) tags are enabled.', 'vbulletin', 'vBulletin Solutions', 1206966101, '3.7.0 Release Candidate 2', 0),
(65372, -1, 'options_options_vm_allowed_bbcode_title', 'cphelptext', 'Allowed BB Code Tags in Visitor Messages', 'vbulletin', 'vBulletin Solutions', 1205422208, '3.7.0 Release Candidate 1', 0),
(65373, -1, 'options_options_welcomepm_text', 'cphelptext', 'If you enable this setting, the user will be sent a private message after registration. Note if you enable Verify Email Address in Registration below, the user must verify their address before a private message will be sent. If you have Moderate New Members enabled above, the private message will be sent when the user is approved.', 'vbulletin', 'vBulletin Solutions', 1145990990, '3.6.0 Beta 1', 0),
(65374, -1, 'options_options_welcomepm_title', 'cphelptext', 'Welcome Private Message', 'vbulletin', 'vBulletin Solutions', 1145990990, '3.6.0 Beta 1', 0),
(65375, -1, 'options_options_wgo_members_text', 'cphelptext', 'Turns the visiting members display on or off.', 'vbulletin', 'vBulletin Solutions', 1334939800, '4.2.0 Alpha 1', 0),
(65376, -1, 'options_options_wgo_members_title', 'cphelptext', 'Enable Display', 'vbulletin', 'vBulletin Solutions', 1334939800, '4.2.0 Alpha 1', 0),
(65377, -1, 'options_options_wgo_members_24_text', 'cphelptext', 'If set to Yes, the who visited display will always show visiting members for the last 24 hours, otherwise the display will reset at each members midnight.', 'vbulletin', 'vBulletin Solutions', 1334939743, '4.2.0 Alpha 1', 0),
(65378, -1, 'options_options_wgo_members_24_title', 'cphelptext', 'Rolling 24 Hours Display', 'vbulletin', 'vBulletin Solutions', 1334939743, '4.2.0 Alpha 1', 0),
(65379, -1, 'options_options_wgo_members_collapse_text', 'cphelptext', 'If enabled, this will cause the who visited display to always load collapsed. This can be useful on a busy forum when the display can get quite large.', 'vbulletin', 'vBulletin Solutions', 1334939569, '4.2.0 Alpha 1', 0),
(65380, -1, 'options_options_wgo_members_collapse_title', 'cphelptext', 'Display Collapse (on Load)', 'vbulletin', 'vBulletin Solutions', 1334939569, '4.2.0 Alpha 1', 0),
(65381, -1, 'options_options_wgo_members_most_text', 'cphelptext', 'This will enable the most members who have visited count. This can only be enabled if you use the rolling 24 hours option.', 'vbulletin', 'vBulletin Solutions', 1334939623, '4.2.0 Alpha 1', 0),
(65382, -1, 'options_options_wgo_members_most_title', 'cphelptext', 'Most Ever Members Option', 'vbulletin', 'vBulletin Solutions', 1334939623, '4.2.0 Alpha 1', 0),
(65383, -1, 'options_options_wgo_members_names_text', 'cphelptext', 'If enabled, the who visited display will list all the members names, otherwise the names will not be displayed.', 'vbulletin', 'vBulletin Solutions', 1334939794, '4.2.0 Alpha 1', 0),
(65384, -1, 'options_options_wgo_members_names_title', 'cphelptext', 'Display Member Names', 'vbulletin', 'vBulletin Solutions', 1334939794, '4.2.0 Alpha 1', 0),
(65385, -1, 'options_options_who_read_text', 'cphelptext', 'If set to No, this turns the Who Read display on off, regardless of any other permissions or settings. Note that this will also disable the recording of the who read data as well.', 'vbulletin', 'vBulletin Solutions', 1336427251, '4.2.0 Alpha 1', 0),
(65386, -1, 'options_options_who_read_title', 'cphelptext', 'Enable Who Read a Thread', 'vbulletin', 'vBulletin Solutions', 1334940994, '4.2.0 Alpha 1', 0),
(65387, -1, 'options_options_wrtauthor_text', 'cphelptext', 'If set, this allows the thread creator to see the who read display, even if their usergroup permissions would not normally allow it. They will not be able to see the display if it is turned off in the forums settings.', 'vbulletin', 'vBulletin Solutions', 1334940384, '4.2.0 Alpha 1', 0),
(65388, -1, 'options_options_wrtauthor_title', 'cphelptext', 'Who Read - Allow Author View', 'vbulletin', 'vBulletin Solutions', 1334940384, '4.2.0 Alpha 1', 0),
(65389, -1, 'options_options_wrtcollapse_text', 'cphelptext', 'If set to Yes, the who read display will always be collapsed on first page load.', 'vbulletin', 'vBulletin Solutions', 1334940465, '4.2.0 Alpha 1', 0),
(65390, -1, 'options_options_wrtcollapse_title', 'cphelptext', 'Who Read - Collapse on Load', 'vbulletin', 'vBulletin Solutions', 1334940465, '4.2.0 Alpha 1', 0),
(65391, -1, 'options_options_wrtlimit_text', 'cphelptext', 'Sets the time limit on the who read display, for example, if you set this to 10, then only members who have read the thread in the last 10 days will be shown. Set this to 0 to display all readers. Bear in mind that this will also be affected by the automatic clean-up routine that deletes old records from the contentread table.', 'vbulletin', 'vBulletin Solutions', 1334940930, '4.2.1 Alpha 1', 0),
(65392, -1, 'options_options_wrtlimit_title', 'cphelptext', 'Who Read - Days Limit', 'vbulletin', 'vBulletin Solutions', 1334940930, '4.2.0 Alpha 1', 0),
(65393, -1, 'options_options_wrtreaders_text', 'cphelptext', 'Determines if the who read a thread display shows the members names. If set to No then names will not be shown.', 'vbulletin', 'vBulletin Solutions', 1334940438, '4.2.0 Alpha 1', 0),
(65394, -1, 'options_options_wrtreaders_title', 'cphelptext', 'Who Read - Names Display', 'vbulletin', 'vBulletin Solutions', 1334940438, '4.2.0 Alpha 1', 0),
(65395, -1, 'options_options_socnet_groups_msg_enabled_text', 'cphelptext', 'If you select this option, members of each group will be able to post messages in the group. Users who are not part of the group will still be able to read the messages.', 'vbulletin', 'vBulletin Solutions', 1201194465, '3.7.0 Beta 5', 0),
(65396, -1, 'options_options_socnet_groups_msg_enabled_title', 'cphelptext', 'Enable Group Messages', 'vbulletin', 'vBulletin Solutions', 1199282205, '3.7.0 Beta 4', 0),
(65397, -1, 'options_options_vb_antispam_key_text', 'cphelptext', 'The Anti-Spam API key is used to enable the scanning service for user generated data. The data will sent to the specified service for identification.<br />\r\n<br />\r\nIf the data is identified as spam, it will be marked for moderation to allow for verification by moderators. You should give your moderators the ''Can Moderate'' permission to allow for this.', 'vbulletin', 'vBulletin Solutions', 1221044432, '3.8.0 Alpha 1', 0),
(65398, -1, 'options_options_vb_antispam_key_title', 'cphelptext', 'vBulletin Anti-Spam Key', 'vbulletin', 'vBulletin Solutions', 1194885285, '3.7.0 Beta 1', 0),
(65399, -1, 'options_options_bbactive_text', 'cphelptext', 'If this is set to "Yes", then users will be able to use your vBulletin. If this is set to "No", then users will see a message notifying them that the site is turned off. You can edit this message in the "Reason For Turning vBulletin Off" option (below).<br /><br />\r\n\r\nNote that admins will still be able to use vBulletin while it is turned off, but they will see a notice on the top and bottom of the screen that says the site is offline.', 'vbulletin', 'vBulletin Solutions', 1314805903, '4.1.7 Beta 1', 0),
(65400, -1, 'options_options_bbactive_title', 'cphelptext', 'vBulletin Active', 'vbulletin', 'vBulletin Solutions', 1314805903, '4.1.7 Beta 1', 0),
(65401, -1, 'options_options_vb_antispam_posts_text', 'cphelptext', 'If a non moderator usergroup has less than this number of posts then all content will be scanned by the Anti-Spam service for possible signs of spam. A value of <em>0</em> will scan all posts.', 'vbulletin', 'vBulletin Solutions', 1194885603, '3.7.0 Beta 1', 0),
(65402, -1, 'options_options_vb_antispam_posts_title', 'cphelptext', 'Spam Scanning Post Threshold', 'vbulletin', 'vBulletin Solutions', 1194885603, '3.7.0 Beta 1', 0),
(65403, -1, 'options_options_bbclosedreason_text', 'cphelptext', 'When vBulletin is turned off, this message will be displayed for users who try to load a page.', 'vbulletin', 'vBulletin Solutions', 1314805903, '4.1.7 Beta 1', 0),
(65404, -1, 'options_options_bbclosedreason_title', 'cphelptext', 'Reason For Turning vBulletin Off', 'vbulletin', 'vBulletin Solutions', 1314805903, '4.1.7 Beta 1', 0),
(65405, -1, 'options_options_bbtitle_text', 'cphelptext', 'This is the title of your forum.  This title appears in the title bar of every forum page and other places.', 'vbulletin', 'vBulletin Solutions', 1243380402, '4.0.0 Alpha 1', 0),
(65406, -1, 'options_options_bbtitle_title', 'cphelptext', 'Forum Name', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(65407, -1, 'options_options_bburl_text', 'cphelptext', 'This is the URL to your vBulletin forum, without the final "/" or "index.php". Examples of correct URLs:<br /><br />\r\n\r\n<i>http://www.example.com</i><br />\r\n<i>http://www.example.com/forum</i><br /><br />\r\n\r\nExamples of incorrect URLs:<br /><br />\r\n\r\n<i>http://www.example.com/</i> (should not have final slash)<br />\r\n<i>http://www.example.com/forum/index.php</i> (should not have final slash or index.php)', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(65408, -1, 'options_options_bburl_title', 'cphelptext', 'Forum URL', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(65409, -1, 'options_options_hometitle_text', 'cphelptext', 'This is the title of your homepage. A link to your homepage appears in the footer of every page by default.  You may also configure the <em>Show Home Link</em> options to display this link in the navigation menu and breadcrumb.', 'vbulletin', 'vBulletin Solutions', 1243380800, '4.0.0 Alpha 1', 0),
(65410, -1, 'options_options_hometitle_title', 'cphelptext', 'Homepage Name', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(65411, -1, 'options_options_homeurl_text', 'cphelptext', 'This is the URL to your homepage. It will be used (along with "Homepage Name") to create a link to your homepage in the footer of every page.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
