(64821, -1, 'image_addcategory_editcategory_title_text', 'cphelptext', 'This is the title of the category. It is displayed with the images in the category to your users.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64822, -1, 'image_addcategory_editcategory_title_title', 'cphelptext', 'Title', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64823, -1, 'image_addcategory_editcategory_displayorder_text', 'cphelptext', 'This determines in what order categories are displayed to the user.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64824, -1, 'image_addcategory_editcategory_displayorder_title', 'cphelptext', 'Display Order', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64825, -1, 'image_edit_text', 'cphelptext', 'This page allows you to edit the settings for this image.  If you have any questions about specific settings please see the individual help topics.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64826, -1, 'image_edit_title', 'cphelptext', 'Edit Avatar / Post Icon / Smilie', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64827, -1, 'image_editcategory_text', 'cphelptext', 'On this screen you can edit an existing image category. Images are displayed by category and you can set permissions per category as to who can use them (except for smilies).', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64828, -1, 'image_editcategory_title', 'cphelptext', 'Edit Avatar / Post Icon / Smilie Category', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64829, -1, 'image_editpermissions_text', 'cphelptext', 'Image categories can be restricted by usergroup on this page.  Simply select "Yes" for groups whom you want to have access to this category and select "No" for groups you don''t.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
