INSERT INTO `phrase` (`phraseid`, `languageid`, `varname`, `fieldname`, `text`, `product`, `username`, `dateline`, `version`, `importphraseid`) VALUES
(64831, -1, 'image_modify_text', 'cphelptext', 'You can manage your images here.  Images can be arranged in categories and their access restricted by post count and usergroup.  Usergroup restrictions are set at the category level while post count restrictions are set at the image level.<br /><br />\r\n\r\nAt the bottom of this page you can click the [Add New Category] link to create a new category for images to go into.  Click the [Show All] link to view all images for all categories.<br /><br />\r\n\r\nBelow is a list of your image categories.  The "Title" column tells you the name of each category.  You can click on the category name to view its images.<br /><br />\r\n\r\nThe "Contains" column tells you how many images are in the corresponding category.<br /><br />\r\n\r\nThe "Display Order" column tells you the display order of each image category.  The display order is used to order the image category listing in this Image Manager as well as on your forums.  You can update the display orders shown on this page by changing the number values and clicking the "Save Display Order" button.<br /><br />\r\n\r\nOn the right side you will find several options for each image category.  The [Mass Move] link takes you to a page where you can select a new category for each image in the corresponding category and submit the changes all at once.<br /><br />\r\n\r\nThe [View] link does the same thing as clicking on the category name to the left, it takes you to a page where you can view all images in the corresponding category.<br /><br />\r\n\r\nThe [Edit] link allows you to edit the title and display order of the corresponding category.<br /><br />\r\n\r\nThe [Delete] link takes you to a page where you can delete the corresponding category and all of its images or mass move the categories images to another category and then delete the original category.<br /><br />\r\n\r\nThe [Permissions] link takes you to a page where you can specify which usergroups can use the images in the corresponding category.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64832, -1, 'image_modify_title', 'cphelptext', 'Avatar / Post Icon / Smilie Manager', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64833, -1, 'image_remove_text', 'cphelptext', 'This is a confirmation that you want to delete this image.  Click the "Yes" button below to perform the deletion.  This action cannot be undone.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64834, -1, 'image_remove_title', 'cphelptext', 'Delete Avatar / Post Icon / Smilie', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64835, -1, 'image_removecategory_text', 'cphelptext', 'This page provides you with options for deleting this category.  First of all, you can choose to delete all images in this category or not.  And second, if you do not choose to delete all images in this category then you can select another category to which this categories images will be moved.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64836, -1, 'image_removecategory_title', 'cphelptext', 'Delete Avatar / Post Icon / Smilie Category', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64837, -1, 'image_upload_text', 'cphelptext', 'On this screen, you can upload one image at a time through your browser for use on your forum. To do this, both your web server and PHP must have permission to write files to disk. If they do not, this will fail. The directory that you are trying to put the file in must be CHMODed 777 (consult your FTP program documentation for how to do this).', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64838, -1, 'image_upload_title', 'cphelptext', 'Upload Avatar / Post Icon / Smilie', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64839, -1, 'image_upload_imagespath_text', 'cphelptext', 'This is the file path that smilies will be uploaded to and read from.  This file path should be readable AND writeable by your web server (usually chmod 0777) so that they may be uploaded and displayed.', 'vbulletin', 'vBulletin Solutions', 1220959514, '3.8.0 Alpha 1', 0),
(64840, -1, 'image_upload_imagespath_title', 'cphelptext', 'Smilie File Path', 'vbulletin', 'vBulletin Solutions', 1220959514, '3.8.0 Alpha 1', 0),
(64841, -1, 'image_upload_upload_text', 'cphelptext', 'Here you can select the file to upload.', 'vbulletin', 'vBulletin Solutions', 1220959448, '3.8.0 Alpha 1', 0),
(64842, -1, 'image_upload_upload_title', 'cphelptext', 'Filename', 'vbulletin', 'vBulletin Solutions', 1220959448, '3.8.0 Alpha 1', 0),
(64843, -1, 'image_upload_imagefile_text', 'cphelptext', 'This is where you select the file from your computer to be uploaded.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64844, -1, 'image_upload_imagefile_title', 'cphelptext', 'Image file', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64845, -1, 'image_viewimages_text', 'cphelptext', 'Below is a gallery of images for the category you selected.  If there are more images in this gallery than are allowed per page (setting at the bottom of the page) then you will see page links at the bottom of the page.  You can click the page links to view the rest of the images in this gallery.<br /><br />\r\n\r\nAt the bottom of this page you will see two links.  Click the [Add] link to add a new image to your forums.  Click the [Edit Categories] link to return to the Image Manager.<br /><br />\r\n\r\n<b>Viewing Images:</b><br />\r\nIf you are viewing images then each image shown in the gallery below will have options associated with it.  Click the [Edit] button to edit the settings for the corresponding image.  Click the [Delete] link to delete the corresponding image.<br /><br />\r\n\r\nThe numbers under each image are the display orders of the images.  These display orders are used to order the listing of images in this Image Manager as well as on the forums.  You can change the displays orders on this page by changing the numbers and clicking the "Save Display Order" button at the bottom.<br /><br />\r\n\r\n<b>Mass Moving Images:</b><br />\r\nIf you are mass moving images then each image shown in the gallery below will have a menu below it.  Select a category from the menu under each image.  When you click the "Mass Move" button the images below will be moved to the categories you selected.<br /><br />\r\n\r\nLeave the menu set to the "blank" option if you don''t want to move an image.  If you want to move all images on this page to the same category then you can select that category in the menu at the bottom which will cause all menus on the page to be set to the category you choose.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64846, -1, 'image_viewimages_title', 'cphelptext', 'View Avatars / Post Icons / Smilies', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64847, -1, 'index_home_text', 'cphelptext', 'On this page you can view several quick stats about your forums and server as well as use the quick admin features which include: Quick User Finder, PHP Function Lookup, MySQL Language Lookup, and Useful Links.  At the bottom of this page are the vBulletin credits.<br /><br />\r\n\r\nIf you have permission to run the "top" command on your server then you will see your server load averages under the "Quick Administration Links" section on this page.  <a href="http://www.vbulletin.com/forum/showpost.php?p=291831&amp;postcount=9" target="_blank">Click here</a> for a detailed explanation of load averages.<br /><br />\r\n\r\nThe Quick User Finder allows you to quickly search for users based on a partial or exact username match.<br /><br />\r\n\r\nThe PHP Function Lookup will search the functions on the PHP site for the search string you enter.  The results will be displayed in this frame.<br /><br />\r\n\r\nThe MySQL Language Lookup will search the documentation on the MySQL site for the search string you enter.  The results will be displayed in this frame.<br /><br />\r\n\r\nThe Useful Links menu contains several useful vBulletin, PHP, and MySQL links that will open in a new window when you select their menu item.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64848, -1, 'index_home_title', 'cphelptext', 'Control Panel Home', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64849, -1, 'language_text', 'cphelptext', '<b>Related General Help Topics:</b><br />\r\n<a href="help.php?s=&amp;do=answer&amp;page=language&amp;pageaction=&amp;option=">Languages</a><br />\r\n<a href="help.php?s=&amp;do=answer&amp;page=phrase&amp;pageaction=&amp;option=">Phrases</a><br /><br />\r\n\r\n<b>Languages:</b><br />\r\nvBulletin''s language system allows you to translate your forum into other languages without editing any templates or files.  This is thanks to the phrase system (see "Phrases" link above) which makes vBulletin''s templates language-independent.  The end result is that you can serve multiple languages from a single style.<br /><br />\r\n\r\nEach phrase can have a translation for each language specified in this Language Manager.  When a forum page is loaded, vBulletin checks what language the current logged in user has selected and parses the phrase variables into the appropriate translated text.<br /><br />\r\n\r\nIn addition to translated phrases, vBulletin''s language system supports translated images (by allowing you to override a style''s images folder setting), and language-specific date / time / number formats.', 'vbulletin', 'vBulletin Solutions', 1205941303, '3.7.0 Release Candidate 1', 0),
(64850, -1, 'language_title', 'cphelptext', 'About Languages', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64851, -1, 'language_add_text', 'cphelptext', 'On this screen you can add a new language. From there you will be able to translate all phrases into this language just as with the default languages that come with vBulletin.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64852, -1, 'language_add_title', 'cphelptext', 'Creating a New Language', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(64853, -1, 'language_add_edit_settings_title_text', 'cphelptext', 'This is the name of the language as displayed in the Administration area as well as to users.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
