INSERT INTO `phrase` (`phraseid`, `languageid`, `varname`, `fieldname`, `text`, `product`, `username`, `dateline`, `version`, `importphraseid`) VALUES
(66077, -1, 'profilefield_add_edit_title_text', 'cphelptext', 'This is the title of the profile field as displayed to your users and in the admin control panel. It can be up to 50 characters long.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66078, -1, 'profilefield_add_edit_title_title', 'cphelptext', 'Title', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66079, -1, 'profilefield_add_edit_description_text', 'cphelptext', 'This is the description of the field as shown to your users. You should describe here what the field is intended for, and if it is required field or not, and/or if it is private from other users or not.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66080, -1, 'profilefield_add_edit_description_title', 'cphelptext', 'Description', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66081, -1, 'profilefield_add_edit_profilefieldcategoryid_text', 'cphelptext', 'It is possible to group custom user profile fields into categories so that they can be logically grouped. The primary purpose of doing this is to allow different categories to be shown in different tabs or blocks on a user''s profile page (in addition to being shown in the ''About Me'' tab).<br />\r\n<br />\r\n<a href="help.php?do=answer&amp;page=profilefield&amp;pageaction=editcat">For further information, see the help entry for the profile field category manager.</a>', 'vbulletin', 'vBulletin Solutions', 1205937526, '3.7.0 Release Candidate 1', 0),
(66082, -1, 'profilefield_add_edit_profilefieldcategoryid_title', 'cphelptext', 'Profile Field Category', 'vbulletin', 'vBulletin Solutions', 1205937422, '3.7.0 Release Candidate 1', 0),
(66083, -1, 'profilefield_add_edit_data_text', 'cphelptext', '<b>Select Fields</b><br />\r\nIn this box, put each value that should appear as an option. Make sure to press Enter after each option or it will not work correctly!<br />\r\n<br />\r\n<b>Radio Fields</b><br />\r\nIn this box, put each value that should appear as a radio button. Make sure to press Enter after each option or it will not work correctly!<br />\r\n<br />\r\n<b>Checkbox Fields</b><br />\r\nIn this box, put each value that should appear as a checkbox. Make sure to press Enter after each option or it will not work correctly!', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66084, -1, 'profilefield_add_edit_data_title', 'cphelptext', 'Multiple Choice Fields', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66085, -1, 'profilefield_add_edit_maxlength_text', 'cphelptext', '<b>Maximum Input (Input, Textarea only)</b><br />\r\nThis setting is how many characters a user is allowed to enter into this field.<br />\r\n<br />\r\n<b>Maximum Input (for Optional Field, Select, Radio only)</b><br />\r\nThis setting is how many characters a user may enter into the optional field.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66086, -1, 'profilefield_add_edit_maxlength_title', 'cphelptext', 'Maximum Input Length', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66087, -1, 'profilefield_add_edit_size_text', 'cphelptext', '<b>Field Length (<i>Input, Textarea only</i>)</b><br />\r\nThis is how many characters long the input should appear on the forms. This should be less than or equal to the maximum number of characters allowed to be entered into this field.<br />\r\n<br />\r\n<b>Field Length (for Optional Field, <i>Select, Radio only</i>)</b><br />\r\nThis setting is how many characters long the input field should appear as on the form. It is recommended that you set this to be equal to or less than the maximum number of characters the user may enter as set above.<br />\r\n<br />\r\n<b>Limit Boxes (<i>Select Multiple only</i>)</b><br />\r\nThis is how many options the user may choose. Set to 0 to allow the user to select all options.<br />\r\n<br />\r\n<b>Limit Boxes (<i>Checkbox only</i>)</b><br />\r\nThis setting is how many checkboxes the user may check total for this field. Enter 0 to allow them to select all checkboxes.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66088, -1, 'profilefield_add_edit_size_title', 'cphelptext', 'Field Length &amp; Limit Boxes', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66089, -1, 'profilefield_add_edit_newtype_text', 'cphelptext', 'Select a field type for this profile field.  Please see the General Topics for examples of each type of profile field.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66090, -1, 'profilefield_add_edit_newtype_title', 'cphelptext', 'Profile Field Type', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66091, -1, 'profilefield_add_edit_height_text', 'cphelptext', 'This is how many rows high the input should be on the forms. It is recommend that you set this to at least 2 for user sanity reasons.<br />\r\n<i>This only applies to Textarea type fields</i>', 'vbulletin', 'vBulletin Solutions', 1152785567, '3.6.0 Release Candidate 2', 0),
(66092, -1, 'profilefield_add_edit_height_title', 'cphelptext', 'Field Height (<i>Textarea only</i>)', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66093, -1, 'profilefield_add_edit_def_text', 'cphelptext', '<b>Select Fields</b><br />\r\nIf you wish to have the first option set as the default choice for this field, set this to Yes. Otherwise, a blank line will be the default. This is useful to force users to select an option at registration if this is a required field.<br />\r\n<br />\r\n<b>Radio Fields</b><br />\r\nIf this is set to Yes, the first radio button will be preselected for the user. Otherwise, none will be selected. This is useful if you wish to force a user to select one if this field is required.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66094, -1, 'profilefield_add_edit_def_title', 'cphelptext', 'Set Default?', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(66095, -1, 'profilefield_add_edit_perline_text', 'cphelptext', 'This setting is how many controls to display per line on forms. Set this to 0 to show all controls on one line.', 'vbulletin', 'vBulletin Solutions', 1199998638, '3.7.0 Beta 4', 0),
(66096, -1, 'profilefield_add_edit_perline_title', 'cphelptext', 'Items Per Line (<i>Radio, Checkbox only</i>)', 'vbulletin', 'vBulletin Solutions', 1147805177, '3.6.0 Beta 1', 0),
(66097, -1, 'profilefield_add_edit_boxheight_text', 'cphelptext', 'This setting is how tall the box should be. If you have 12 options and want 3 of them displayed without the user having to use the scrollbar, set this to 3. It is recommended that you set this to at least 3 for user sanity reasons.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
