(67029, -1, 'usergroup_add_edit_canmanageothersusernotes_text', 'cphelptext', 'This permission allows members of this group to manage (edit / delete) user notes for other users.  You should only grant this permission to users you trust.<br /><br />\r\n\r\nUser notes can be managed on the screen that lists a user''s notes - click the <i>User Notes: [View]</i> link when viewing a user''s profile page on the frontend.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(67030, -1, 'usergroup_add_edit_canmanageothersusernotes_title', 'cphelptext', 'Can Manage User Notes About Others', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(67031, -1, 'usergroup_add_edit_canpostothersusernotes_text', 'cphelptext', 'This permission allows members of this group to post user notes about other members.<br /><br />\r\n\r\nUser notes can be posted on the screen that lists a user''s notes.  First click the <i>User Notes: [View]</i> link when viewing a user''s profile page on the frontend, then click the <i>Post New User Note</i> link.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
