(67043, -1, 'usergroup_add_edit_canuserep_text', 'cphelptext', 'This permission allows the user to leave reputation comments for other users.  Reputation must also be enabled in the vBulletin Options.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(67044, -1, 'usergroup_add_edit_canuserep_title', 'cphelptext', 'Can Use Reputation', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
(67045, -1, 'usergroup_add_edit_canseeownrep_text', 'cphelptext', 'This permission allows the user to see which members left the comments that appear in the user''s User CP. If the user has chosen to hide their reputation or the reputation system is disabled, the list of comments will not appear in any case.', 'vbulletin', 'vBulletin Solutions', 0, '', 0),
