(67051, -1, 'usergroup_add_edit_sigmaxrawchars_text', 'cphelptext', 'This is the maximum number of characters that can a user in this group may use in his or her signature. This includes all markup codes that were used.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(67052, -1, 'usergroup_add_edit_sigmaxrawchars_title', 'cphelptext', 'Maximum Characters in Signature Including BB Code Markup', 'vbulletin', 'vBulletin Solutions', 1205493460, '3.7.0 Release Candidate 1', 0),
(67053, -1, 'usergroup_add_edit_sigmaxchars_text', 'cphelptext', 'This is the maximum number of characters users in this group may place in their signatures, not including any BB code used to format the signature. In most cases, this is very close to the number of displayable characters in the signature.', 'vbulletin', 'vBulletin Solutions', 1206966128, '3.7.0 Release Candidate 2', 0),
(67054, -1, 'usergroup_add_edit_sigmaxchars_title', 'cphelptext', 'Maximum Characters in Signature Excluding BB Code Markup', 'vbulletin', 'vBulletin Solutions', 1205493460, '3.7.0 Release Candidate 1', 0),
(67055, -1, 'usergroup_add_edit_sigmaxlines_text', 'cphelptext', 'This is the maximum number of lines users of this group may have in their signatures. Unfortunately, due to browser and style variations, this is not an exact science and should only be used as a rough guide.<br />\r\n<br />\r\nSoft linewrapping can cause one contiguous string of text to be counted as multiple lines. The number of characters that are allowed before this text is treated as multiple lines is controlled by the &quot;<a href="options.php?do=options&amp;dogroup=user">Signature Soft-Linebreak Character Limit</a>&quot; option. Text that is not the default size is scaled to count as more or less characters than normal.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(67056, -1, 'usergroup_add_edit_sigmaxlines_title', 'cphelptext', 'Maximum Lines in Signature', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(67057, -1, 'usergroup_add_edit_canbbcode_text', 'cphelptext', 'This is the global switch for BB codes in the signatures of users in this group. Individual BB codes may be controlled by the additional permissions displayed below.<br />\r\n<br />\r\nNote that custom BB codes cannot be controlled by any permission; you can only disable their use in signatures by disabling all BB codes in signatures.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(67058, -1, 'usergroup_add_edit_canbbcode_title', 'cphelptext', 'Allow BB Code', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
(67059, -1, 'usergroup_add_edit_canbbcodebasic_text', 'cphelptext', 'This controls whether [b] (Bold), [i] (Italic), and [u] (Underline) tags may be used in signatures by users of this group. If they attempt to use any of these and this setting is set to "No", they will receive an error while saving their signature.', 'vbulletin', 'vBulletin Solutions', 1142519066, '3.6.0 Beta 1', 0),
