(76754, -1, 'setting_bburl_basepath_desc', 'vbsettings', 'If this is set to ''Yes'' the Forum URL will always be used for links instead of the current URL.  This ensures that links are consistent but may not be desirable if you use multiple domains or a mix of http and https. <br/><br/>\r\n\r\nIf the URLs for the different vBulletin components are set below, this option must be set to yes.', 'vbulletin', 'vBulletin Solutions', 1294335806, '4.1.2', 0),
(76755, -1, 'setting_bburl_basepath_title', 'vbsettings', 'Always use Forum URL as Base Path', 'vbulletin', 'vBulletin Solutions', 1294335806, '4.1.2', 0),
(76756, -1, 'setting_bburl_desc', 'vbsettings', 'URL of your forum.<br />\r\nNote: do not add a trailing slash. (''/'')', 'vbulletin', 'vBulletin Solutions', 1267484143, '4.0.4', 0),
