(76782, -1, 'setting_contactusemail_desc', 'vbsettings', 'Email address for receiving the emails from Contact Us page by default. If you leave this option empty, Webmaster''s Email will be used.', 'vbulletin', 'vBulletin Solutions', 1266932197, '4.0.3', 0),
(76783, -1, 'setting_contactusemail_title', 'vbsettings', 'Contact Us Email', 'vbulletin', 'vBulletin Solutions', 1266932197, '4.0.3', 0),
(76784, -1, 'setting_contactuslink_desc', 'vbsettings', 'Link for contacting the site admin. To use the built-in email form, specify <b>sendmessage.php</b> otherwise use something such as ''mailto:webmaster@example.com'' or your own custom form. This appears at the bottom of every page.', 'vbulletin', '', 0, '', 0),
